        for(String id: idsList)
        {

            ProvisioningPlan plan = new ProvisioningPlan();
            Account account = new Account();

            Identity identity = context.getObjectById(Identity.class, id);
            Custom custom = context.getObjectByName(Custom.class, "Triumvir-standard-leaver");
            List exceptionList = custom.get("acount-disable-exception-list");
            if(exceptionList.contains(identity.getName())){
                System.out.println("Excluding ... " + identity.getName());
            }
            else{
                plan = account.getProvisioninPlan(identity, context, exceptionList);
                System.out.println(plan.toXml());
                ProvisioningExecutor provExecutor = new ProvisioningExecutor();
                provExecutor.executeProvisioning(plan, context);
            }   
        }   
   }
